    """Test Cases"""

    def test_communication_key_length(self) -> None:
        """Tests cases of invalid communication key length"""
        with self.assertRaises(frappe.ValidationError):
            token = """
            
            """
            cmckey = frappe.get_doc(COMMUNICATION_KEYS_DOCTYPE_NAME)

